actionCellButton.customFunction:
var attributeService = widgetContext.attributeService;
var entityType = entityId.entityType;

if (entityType === 'ASSET') { //floor
    openState('floor_details');
} else if (entityType === 'CUSTOMER') {
    attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', ['type'], {ignoreLoading: true}).subscribe(
      function(attributes) {
          if (attributes && attributes.length) {
              var type = attributes[0].value;
              if (type === 'building_management_company') {
                  openState('customer_details');
              } else if (type === 'building_management_customer') {
                  openState('sub_customer_details');
              } else if (type === 'building') {
                  openState('building_details');
              }
          }
      }
    );
}

function openState(stateId) {
    if (stateId) {
          var params = {
                entityId: entityId,
                entityName: entityName
          };
          widgetContext.stateController.updateState(stateId, params, false);
    }
}

