self.onInit = function() {


options = {
  series: [44, 55, 13, 33],
  dataLabels: {
    enabled: true,
    formatter: function (val) {
      return val.toFixed(2) + "%"
    },
  },
  labels: ['Apple', 'Mango', 'Orange', 'Watermelon'],
    chart: {
    type: 'donut'
  }
}

    var ctx = $('#pieChart', self.ctx.$container);
    self.ctx.chart = new ApexCharts(document
        .querySelector("#piechart"), options);

    self.ctx.chart.render();
}

